import React from 'react';
import {
  Select,
  Button, MenuItem, Card
} from '@material-ui/core';
import { object } from 'prop-types';
import styled from 'styled-components';
import Controllers from '../../../controllers/Controllers';

const Container = styled.div`
  display:flex;
  justify-content: center;
  align-items: center;
  height:100%;
  flex-direction: column;
  padding: 1em;
`;

const View = (props) => {
  const { model } = props;
  return (
    <Container>
      <Card
        style = {{
          width: '100%',
          maxWidth: 400,
          padding: '1em'
        }}
      >
        <Select
          fullWidth
          onChange={model.setRole}
          value={model.role.title}
        >
          {model.roles.map((role) => (
            <MenuItem
              key={role.title}
              value={role.title}
            >
              {role.title}
            </MenuItem>
          ))}
        </Select>

        <Button
          fullWidth
          color = 'primary'
          variant='contained'
          style = {{
            marginTop:'1em'
          }}
          onClick={() => {
            Controllers.signin(
              model.role.email,
              model.role.password
            );
          }}
        >
          Sign In
        </Button>
      </Card>
    </Container>
  );
};

View.propTypes = {
  model: object
};

export default View;
