import React from 'react';
import {
  Select,
  Button,
  MenuItem,
  Card,
  CircularProgress,
  CardHeader,
  Typography
} from '@material-ui/core';
import { object } from 'prop-types';
import styled from 'styled-components';
import Controllers from '../../../controllers/Controllers';

const Container = styled.div`
  display:flex;
  justify-content: center;
  align-items: center;
  height:100%;
  flex-direction: column;
  padding: 1em;
`;

const View = (props) => {
  const { model } = props;
  return (
    <Container>
      <Card
        style={{
          width: '100%',
          maxWidth: 400,
          padding: '1em'
        }}
      >
        <Typography
          variant="h2"
          gutterBottom
        >
          Demo user login
        </Typography>
        <Typography
          variant="subtitle1"
        >
          Demo user can be used to demo the working of the bug tracker
        </Typography>
        <Select
          fullWidth
          onChange={model.setRole}
          value={model.role.title}
          variant='outlined'
        >
          {model.roles.map((role) => (
            <MenuItem
              key={role.title}
              value={role.title}
            >
              {role.title}
            </MenuItem>
          ))}
        </Select>

        { model.loading
          ? (
            <CircularProgress
              size="1em"
              style={{
                marginTop: '1em'
              }}
            />
          )
          : (
            <Button
              fullWidth
              color="primary"
              variant="contained"
              style={{
                marginTop: '1em'
              }}
              onClick={() => {
                model.setLoadingTrue();
                Controllers.signin(
                  model.role.email,
                  model.role.password
                );
              }}
            >
              Sign In
            </Button>
          )}
      </Card>
    </Container>
  );
};

View.propTypes = {
  model: object
};

export default View;
